INSERT INTO `tskf_pp_login_builder` (`id`, `title`, `structure`, `css`, `date`) VALUES
(1, 'Tiskfotek.eu', '<div class="sp-pp">\r\n  <div class="container">\r\n    <h1>Tiskfotek.eu</h1>\r\n    <h2>Přihlášení</h2>\r\n    <fieldset class="form-fieldset ui-input __first">\r\n      [login-username id="username"]\r\n      <label for="username">\r\n        <span data-text="Uživatelské jméno">Uživatelské jméno</span>\r\n      </label>\r\n    </fieldset>\r\n\r\n    <fieldset class="form-fieldset ui-input __second">\r\n      [login-password id="password"]\r\n      <label for="password">\r\n        <span data-text="Heslo">Heslo</span>\r\n      </label>\r\n    </fieldset>\r\n\r\n    <div class="form-footer">\r\n       [login-submit class="btn" value="Log In"]\r\n    </div>\r\n  </div>\r\n</div>', '/* css class for the login generated errors */\r\n.profilepress-login-status {\r\n  background-color: #8f5db7;\r\n  color: #ffffff;\r\n  max-width: 342px;\r\n  margin: 1px auto;\r\n  border: medium none;\r\n  font-size: 15px;\r\n  font-weight: normal;\r\n  border-radius: 4px;\r\n  text-align: center;\r\n  line-height: 1.4;\r\n  padding: 8px 5px;\r\n}\r\n\r\n.profilepress-login-status a {\r\n  color: #ea9629 !important;\r\n}\r\n\r\ndiv.sp-pp h1 {\r\n  color: #fff;\r\n  font-family: "Texta", "Avenir Next", "Avenir", "Helvetica Neue", Helvetica, Arial, sans-serif;\r\n  font-weight: 700;\r\n  text-transform: uppercase;\r\n  letter-spacing: 4px;\r\n  text-align: center;\r\n}\r\n\r\ndiv.sp-pp h2 {\r\n  font-weight: 400;\r\n  font-size: 24px;\r\n  color: rgba(255, 255, 255, 0.4);\r\n}\r\n\r\ndiv.sp-pp .container {\r\n  font-family: "Avenir Next", "Avenir", "Helvetica Neue", Helvetica, Arial, sans-serif;\r\n  font-size: 16px;\r\n  color: #ffffff;\r\n  line-height: 1.5;\r\n  background-color: #8f5db7;\r\n  max-width: 320px;\r\n  margin: 0 auto;\r\n  padding: 20px 16px 40px 16px;\r\n  text-align: center;\r\n}\r\n\r\ndiv.sp-pp .form-footer {\r\n  margin-top: 1em;\r\n}\r\n\r\ndiv.sp-pp .ui-input {\r\n  position: relative;\r\n  padding: 0;\r\n  border: 0;\r\n}\r\n\r\ndiv.sp-pp .ui-input input {\r\n  border: 0;\r\n  color: #fff;\r\n  background: none;\r\n  padding: 16px 0 16px 0;\r\n  font-size: 24px;\r\n  outline: 0;\r\n  width: 100%;\r\n  tap-highlight-color: transparent;\r\n  touch-callout: none;\r\n}\r\n\r\ndiv.sp-pp .ui-input input + label {\r\n  position: relative;\r\n  display: block;\r\n  padding: 8px 0 8px 0;\r\n  text-transform: uppercase;\r\n  font-size: 14px;\r\n  letter-spacing: .0875em;\r\n  font-weight: 500;\r\n  text-align: left;\r\n}\r\n\r\ndiv.sp-pp .ui-input input + label::before,\r\ndiv.sp-pp .ui-input input + label::after {\r\n  position: absolute;\r\n  top: 0;\r\n  left: 0;\r\n  content: "";\r\n  width: 100%;\r\n  height: 1px;\r\n}\r\n\r\n.ui-input input + label::before {\r\n  background-color: rgba(255, 255, 255, 0.2);\r\n}\r\n\r\ndiv.sp-pp .ui-input input + label::after {\r\n  transform: scaleX(0);\r\n  transform-origin: left;\r\n  transition: transform 300ms cubic-bezier(0.215, 0.61, 0.355, 1);\r\n  background-color: #6EB1FF;\r\n  height: 2px;\r\n}\r\n\r\ndiv.sp-pp .ui-input input + label span {\r\n  position: relative;\r\n  color: rgba(255, 255, 255, 0.2);\r\n  transition: color 300ms cubic-bezier(0.215, 0.61, 0.355, 1);\r\n}\r\n\r\ndiv.sp-pp .ui-input input + label span::after {\r\n  content: attr(data-text);\r\n  position: absolute;\r\n  overflow: hidden;\r\n  left: 0;\r\n  transform: scaleX(1);\r\n  white-space: nowrap;\r\n  color: #fff;\r\n  background-image: linear-gradient(to right, #4A90E2 50%, rgba(255, 255, 255, 0) 0%);\r\n  background-position: 100% 50%;\r\n  background-size: 200%;\r\n  -webkit-background-clip: text;\r\n  -webkit-text-fill-color: transparent;\r\n  backface-visibility: hidden;\r\n  perspective: 1000;\r\n  transform: translateZ(0);\r\n  transition: background-position 300ms cubic-bezier(0.215, 0.61, 0.355, 1);\r\n}\r\n\r\ndiv.sp-pp .ui-input input:focus + label::after,\r\ndiv.sp-pp .ui-input input.error + label::after,\r\ndiv.sp-pp .ui-input input:invalid + label::after,\r\ndiv.sp-pp .ui-input